CREATE TABLE IF NOT EXISTS "__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL,
    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

START TRANSACTION;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20250806193904_Tablas') THEN
    CREATE TABLE "HistoryRates" (
        "ID" integer GENERATED BY DEFAULT AS IDENTITY,
        "PrestamoID" text,
        "TasaH" numeric NOT NULL,
        "FechaCreacion" text NOT NULL,
        CONSTRAINT "PK_HistoryRates" PRIMARY KEY ("ID")
    );
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20250806193904_Tablas') THEN
    CREATE TABLE "MasterBanks" (
        "ID" integer GENERATED BY DEFAULT AS IDENTITY,
        "BankID" text,
        "Bank_Name" text,
        "Status" boolean,
        "FechaCreacion" text NOT NULL,
        CONSTRAINT "PK_MasterBanks" PRIMARY KEY ("ID")
    );
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20250806193904_Tablas') THEN
    INSERT INTO "__EFMigrationsHistory" ("MigrationId", "ProductVersion")
    VALUES ('20250806193904_Tablas', '9.0.7');
    END IF;
END $EF$;
COMMIT;

